pragma solidity ^0.8.0;

import "./libraries/Errors.sol";
import "./dependencies/Ownable.sol";
import "./interfaces/IERC20WithPermit.sol";

contract PegasusUSD is IERC20WithPermit, Ownable {
	string public constant name = "Pegasus Stablecoin";
	string public constant symbol = "pUSD";
	uint8 public constant decimals = 18;

	address internal minter;

	uint public totalSupply;
	mapping(address => uint) public balanceOf;
	mapping(address => mapping(address => uint)) public allowance;

	bytes32 public DOMAIN_SEPARATOR;
	bytes32 public constant PERMIT_TYPEHASH =
		0x6e71edae12b1b97f4d1f60370fef10105fa2faae0126114a169c64845d6126c9;
	mapping(address => uint) public nonces;

	constructor() {
		DOMAIN_SEPARATOR = keccak256(
			abi.encode(
				keccak256(
					"EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
				),
				keccak256(bytes(name)),
				keccak256(bytes("1")),
				block.chainid,
				address(this)
			)
		);
		minter = msg.sender;
	}

	modifier onlyMinter() {
		require(msg.sender == minter, Errors.NOT_AUTHORISED);
		_;
	}

	function updateMinter(address newMinter) external onlyOwner {
		minter = newMinter;
	}

	function mint(address to, uint value) external onlyMinter {
		totalSupply = totalSupply + value;
		balanceOf[to] = balanceOf[to] + value;
		emit Transfer(address(0), to, value);
	}

	function burn(address from, uint value) external onlyMinter {
		balanceOf[from] = balanceOf[from] - value;
		totalSupply = totalSupply - value;
		emit Transfer(from, address(0), value);
	}

	function _approve(address owner, address spender, uint value) private {
		allowance[owner][spender] = value;
		emit Approval(owner, spender, value);
	}

	function _transfer(address from, address to, uint value) private {
		balanceOf[from] = balanceOf[from] - value;
		balanceOf[to] = balanceOf[to] + value;
		emit Transfer(from, to, value);
	}

	function approve(address spender, uint value) external returns (bool) {
		_approve(msg.sender, spender, value);
		return true;
	}

	function transfer(address to, uint value) external returns (bool) {
		_transfer(msg.sender, to, value);
		return true;
	}

	function transferFrom(
		address from,
		address to,
		uint value
	) external returns (bool) {
		if (allowance[from][msg.sender] != type(uint256).max) {
			allowance[from][msg.sender] = allowance[from][msg.sender] - value;
		}
		_transfer(from, to, value);
		return true;
	}

	function permit(
		address owner,
		address spender,
		uint value,
		uint deadline,
		uint8 v,
		bytes32 r,
		bytes32 s
	) external {
		require(deadline >= block.timestamp, Errors.DEADLINE_CROSSED);
		bytes32 digest = keccak256(
			abi.encodePacked(
				"\x19\x01",
				DOMAIN_SEPARATOR,
				keccak256(
					abi.encode(
						PERMIT_TYPEHASH,
						owner,
						spender,
						value,
						nonces[owner]++,
						deadline
					)
				)
			)
		);
		address recoveredAddress = ecrecover(digest, v, r, s);
		require(
			recoveredAddress != address(0) && recoveredAddress == owner,
			Errors.INVALID_SIGNATURE
		);
		_approve(owner, spender, value);
	}
}
